import React from 'react';

import Panel from '@vkontakte/vkui/dist/components/Panel/Panel';
import PanelHeader from '@vkontakte/vkui/dist/components/PanelHeader/PanelHeader';
import Button from '@vkontakte/vkui/dist/components/Button/Button';
import Div from '@vkontakte/vkui/dist/components/Div/Div';
import Group from '@vkontakte/vkui/dist/components/Group/Group';
import Header from '@vkontakte/vkui/dist/components/Header/Header';
import Card from '@vkontakte/vkui/dist/components/Card/Card';
import CardGrid from '@vkontakte/vkui/dist/components/CardGrid/CardGrid';
import '../fonts/montserrat.ttf'
import './style.css';



function changeTime() {
  var hour = moment().format("HH");
  var minute = moment().format("mm");
  var second = moment().format("ss");

  var H1 = document.getElementById("hour-1");
  var H2 = document.getElementById("hour-2");
  var M1 = document.getElementById("minute-1");
  var M2 = document.getElementById("minute-2");
  var S1 = document.getElementById("second-1");
  var S2 = document.getElementById("second-2");

  changeTxt(H1, hour.substr(0,1));
  changeTxt(H2, hour.substr(1,1));
  changeTxt(M1, minute.substr(0,1));
  changeTxt(M2, minute.substr(1,1));
  changeTxt(S1, second.substr(0,1));
  changeTxt(S2, second.substr(1,1));

  setTimeout(changeTime, 1000);
}

function changeTxt(obj, txt) {
  if (obj.innerText != txt) {
    obj.className = "hide";
    setTimeout(function() {
      obj.innerText = txt;
      obj.className = "show";
    }, 500);
  }
}
changeTime();



const home = props => (
    <Panel id={props.id}> 
        <PanelHeader>
            Time of Life
        </PanelHeader>
        <Group separator="hide">
      <CardGrid>
        <Card size="l">
          <div style={{ height: 96 }} className="font" > 
          <div class="digital-clock">
      <div class="hour">
        <span id="hour-1"></span>
        <span id="hour-2"></span>
      </div>
      <div class="minute">
        <span id="minute-1"></span>
        <span id="minute-2"></span>
      </div>
      <div class="second">
        <span id="second-1"></span>
        <span id="second-2"></span>
      </div>
    </div>
          
          </div>
        </Card>
      </CardGrid>
    </Group>
            <Div>
                <Button onClick={() => props.UpdateTheme()} size="xl" mode="secondary">Theme</Button>
            </Div>
        <Div>
            <Button onClick={() => props.goToPage("second")} size="xl" mode="secondary">Перейти на вторую панель</Button>
        </Div>
    </Panel>
);

export default home;
